<?php $sliderurl = get_post_meta( get_the_ID(),'rsris_snm_link', true );if($sliderurl == '')	$sliderurl = "#";$content = get_the_title();$content = strip_tags($content);$summary = $this->snm_truncate($content, 200);?><div class="owl-item" >	<div class="css-demo">		<?php if($sliderurl != ''){ ?>			<a class="img" title="<?php echo $summary; ?>" href="<?php echo $sliderurl; ?>">				<?php the_post_thumbnail('large', array('title'=>$summary)); ?>			</a>		<?php }else{					the_post_thumbnail('large', array('title'=>$summary)); 		} ?>		<span>			<?php if($sliderurl != ''){ ?>				<a title="<?php echo $summary; ?>" href="<?php echo $sliderurl; ?>"><?php echo $summary; ?></a>			<?php }else{				echo $summary;				} ?>		</span>	</div></div>